ter-link>                        </div>                    </div>                </div>            </nav>        </main>    </div></template><script>    export default {        data() {            return {                quantityStr: 15,                thisPage: 1,                nextPage: null,                showRedact: false,                leftPage: 1,                rightPage: 2,                transition: true,                newNumber: null,                firstDownload: true,            }        },        watch: {            '$store.state.page': {                immediate: false,                handler() {                    if (this.firstDownload) {                        this.thisPage = this.$store.state.page;                        if(this.thisPage != 1){                            this.leftPage = this.thisPage - 1                        }                        this.firstDownload = false;                        if (this.thisPage != this.qtPage) {                            this.rightPage = this.thisPage + 1                        }                    }                }            }        },        computed: {            qtPage: function () {                return this.$store.state.qtPage            },            installEvent (){                return this.$store.state.installApp            },            canInstall(){                return this.$store.getters.statusInstall            }        },        methods: {            goRight() {                if (this.rightPage === this.qtPage) {                    this.thisPage = this.rightPage                    this.leftPage = this.rightPage - 1                    return                } else if(this.qtPage === 1) {                    return                }                this.rightPage++                this.thisPage++                this.leftPage = this.thisPage - 1;            },            goLeft() {                if (this.leftPage === 1) {                    this.rightPage = 2                    this.thisPage = 1                    return                }                this.leftPage--;                this.thisPage = this.thisPage - 1                this.rightPage = this.thisPage + 1            },            addNumber() {                this.showRedact = true            },            cancelRedact() {                this.showRedact = false                this.newNumber = null            },            onInstall(){                this.installEvent.prompt()            },            async confirmRedact(number) {                debugger                await this.$store.commit('addNumber', +number)                this.newNumber = null                this.showRedact = false            }        },        mounted() {        },        created(){        }    }</script>